import React, { Component } from 'react';
import logo from '../logo/WhatTimeIsItRightNow_logo.png';

export default class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date()};
    }

    componentDidMount() {
        this.timerID = setInterval(
           () => this.tick(),
           1000
       );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    render() {
        return (
            <div>
                <Logo />
                <Clock date={this.state.date} />
            </div>
        );
    }
}

function Clock(props) {
    return (
        <div className='Clock'>
            <LocaleDate date={props.date} />
            <LocaleTime date={props.date} />
        </div>
    )
}

function LocaleDate(props) {

    const element = React.createElement(
        'div',
        {className: 'localDate'},
        `${props.date.getDay()}`
    );
    return element;
}

function LocaleTime(props) {
    const element = React.createElement(
        'div',
        {className: 'localTime'},
        `${props.date.toLocaleTimeString()}`
    );
    return element;
}

function Logo() {
    return (<h1><img src={logo} alt='WhatTimeIsItRightNow Logo' /></h1>);
}
